package aspireLabBook2;

public class QuotientRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 23;
		int y = 10;
		int q = x / y;
		int r = x % y;
		System.out.println("The quotient is: " + q);
		System.out.println("The remaider is: " + r);
		
	}

}